import React, { useState } from 'react'
import "../lateral-menu/LatMenuCliente.css";

export const LatMenuCliente = () => {

  const [selectedMenuLat, setSelectedMenuLat] = useState(1);

  const renderMenuLat = () => {
    switch (selectedMenuLat) {
      case 1: return <div>{MenuHola()}</div>;
      case 2: return <h1>2</h1>;
      case 3: return <h1>3</h1>;
      default: return <h1>Holaa</h1>;
    }
  }


  const MenuHola = () => {

    return (

      <div>
        <ul>
          <li> <a href='/#'>Esteban</a></li>

          <li> <a href='/#'>Gonzalo</a></li>

          <li> <a href='/#'>Jara</a></li>
          <li> <a href='/#'>Gonz√°lez</a></li>
          <li> <a href='/#'>Salir</a></li>
        </ul>
      </div>

    );

  };


  const subMenus = [
    {
      id: 1, subMenu: [
        { path: '/subMenu1-A', label: 'subMenu1-A' },
        { path: '/subMenu1-B', label: 'subMenu1-B' },
        { path: '/subMenu1-C', label: 'subMenu1-C' },
        { path: '/subMenu1-D', label: 'subMenu1-D' },
      ]
    },

    {
      id: 2, subMenu: [
        { path: '/subMenu2-A', label: 'subMenu2-A' },
        { path: '/subMenu2-B', label: 'subMenu2-B' },
        { path: '/subMenu2-C', label: 'subMenu2-C' },
        { path: '/subMenu2-D', label: 'subMenu2-D' }
      ]
    }

  ]


  const navLinksPrincipalMenu = {
    id: 1, subMenu: [
      { path: '/clientes', label: 'Clientes', subMenu: 1 },
      { path: '/ordenes-trabajo', label: 'Ordenes de trabajo', subMenu: 2 },
    ]
  };



  // Find the active subMenu based on selectedMenuLat
  const activeSubMenu = subMenus.find(menu => menu.id === selectedMenuLat);

  return (
    <div className='lat-menu'>
      <ul>
        <li><a href="#!" onClick={() => setSelectedMenuLat(1)}>Clientes</a></li>
        <li><a href="#!" onClick={() => setSelectedMenuLat(2)}>Ordenes de trabajo</a></li>
        {selectedMenuLat && (
          <ul>
            {activeSubMenu?.subMenu.map(item => (
              <li key={item.path}>
                <a href={item.path}>{item.label}</a>
              </li>
            ))}
          </ul>
        )}
      </ul>
    </div>
  );


  /*return (
    <div className='lat-menu'>
        <ul>
            <li> <a href='/#' onClick={()=> setSelectedMenuLat(1)}>Listar Clientes</a></li>

            {selectedMenuLat === 1 ? <div>{MenuHola()}</div> : ""}
            <li> <a href='/#' onClick={()=> setSelectedMenuLat(2)}>Agregar Clientes</a></li>
            {renderMenuLat()}
            <li> <a href='/#'>Quitar Clientes</a></li>
            <li> <a href='/#'>Modificar Clientes</a></li>
            <li> <a href='/#'>Salir</a></li>
        </ul>
    </div>
  )*/
}
